
public class declaracionOperadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=8;
		int c=2;
		final double pulgada=2.54; //declaracion de variable constante
		double cm=10.0;
		
		double resultado;
		resultado=a+b-c;
		System.out.println(resultado);
		resultado=(b*c)/a; 
		System.out.println(resultado);
		resultado=cm/pulgada;
		System.out.println(resultado);

	}

}
